  const API_URL = "https://api.adviceslip.com/advice"
  const [quote, setQuote] = useState()

  useEffect(() => {
    axios.get(API_URL)
      .then(res => {
        console.log(res);
      })
  }, [])


  return (
    <div className="app">
      <div className="card">
        <div className="heading"><h3>This is some awesome quote</h3></div>
        <span>
          <button className="btn">tweet</button>
          <button className="btn">tumblr</button>
          <button className="btn">give me a quote</button>
        </span>

      </div>
    </div>
  );
}

export default App;
